  const doors = [];
  const openedDoors = [];
  for (let i = 0; i < numDoors; i++) {
    doors[i] = false; // create doors
  }
  for (let i = 1; i <= numDoors; i++) {
    for (let i2 = i - 1; i2 < numDoors; i2 += i) {
      doors[i2] = !doors[i2]; // toggle doors
    }
  }
  for (let i = 0; i < numDoors; i++) {
    if (doors[i]) {
      openedDoors.push(i + 1);
    }
  }

  return openedDoors;
}

